import classes from './UserInput.module.css'
import {useState} from 'react'
import TodoList from './TodoList';
import { v4 as uuidv4 } from 'uuid';
const MAX_INPUT_LENGTH = 30;



const UserInput = () => {

    const [newTask,setNewTask] = useState("");
    const [listData,setListData] = useState([]);


    const addInputHandler = (event) => {  
     const inputvalue = event.target.value

     if (inputvalue.length >=MAX_INPUT_LENGTH)
            setNewTask(inputvalue)

    }

    const addListData = () => {
        const newListData = {id:uuidv4(),data:newTask}
         setListData([...listData, newListData])
         console.log(listData);
    }

    const formSubmitHandler = (event) => {

        event.preventDefault();
    }

    const deleteItemHandler = id => {

        setListData(listData.filter(item => item.id !== id))
    }

    const completeItemHandler = (index) => {

    }

    const inputForm =   (

        <form className={classes.todo}
            onSubmit={formSubmitHandler}
            >
                <input 
                type='text' 
                className={classes.input} 
                placeholder='Enter your task here'
                onChange={addInputHandler}
                >
                </input>

                <button 
                type='submit' 
                className={classes.button}
                onClick={addListData}
            >
                Add
                </button>

        </form>)

    return (
        <>
        {inputForm}
        {listData.map((todo,index) => (
        <TodoList data={todo} key={todo.id} deleteItem={deleteItemHandler}/>))}

        </>
    );
};

export default UserInput;
